.highlight-purple, .highlight-yellow, .highlight-interactive, .highlight-discover, .highlight-blog {
    position: relative;
    display: inline-block;
}

.highlight-purple::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: -5%;
    right: -5%;
    height: 15px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 20"><path d="M0 10 C 20 0, 40 0, 50 10 S 70 20, 100 10" stroke="%23A78BFA" stroke-width="4" fill="none" stroke-linecap="round"/></svg>');
    background-repeat: no-repeat;
    background-size: 100% 100%;
    z-index: -1;
}

.highlight-yellow::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: -5%;
    right: -5%;
    height: 20px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 20"><path d="M0 10 Q 25 20, 50 10 T 100 10" stroke="%23FBBF24" stroke-width="5" fill="none" stroke-linecap="round"/></svg>');
    background-repeat: no-repeat;
    background-size: 100% 100%;
    z-index: -1;
}

.highlight-interactive::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: -2%;
    right: -2%;
    height: 12px;
    background-color: #A78BFA; /* purple-400 */
    opacity: 0.3;
    z-index: -1;
    border-radius: 4px;
}

.highlight-discover::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: -2%;
    right: -2%;
    height: 100%;
    background-color: #FBBF24; /* yellow-400 */
    opacity: 0.3;
    z-index: -1;
    border-radius: 8px;
    transform: rotate(-1deg);
}

.highlight-blog::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: -5%;
    right: -5%;
    height: 15px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 20"><path d="M0 10 C 20 0, 40 0, 50 10 S 70 20, 100 10" stroke="%23A78BFA" stroke-width="4" fill="none" stroke-linecap="round"/></svg>');
    background-repeat: no-repeat;
    background-size: 100% 100%;
    z-index: -1;
}

document.addEventListener('DOMContentLoaded', function() {

    // Mobile Menu Toggle
    const menuBtn = document.getElementById('menu-btn');
    const mobileMenu = document.getElementById('mobile-menu');
    menuBtn.addEventListener('click', () => {
        mobileMenu.classList.toggle('hidden');
    });

    // Teacher Carousel
    const carousel = document.getElementById('teacher-carousel');
    const prevBtn = document.getElementById('prev-btn');
    const nextBtn = document.getElementById('next-btn');
    const teacherCards = document.querySelectorAll('.teacher-card');

    if (carousel) {
        let currentIndex = 0;
        let itemsToShow = window.innerWidth < 768 ? 1 : (window.innerWidth < 1024 ? 2 : 4);
        const totalItems = teacherCards.length;

        function updateCarousel() {
            const itemWidth = carousel.querySelector('.teacher-card').offsetWidth;
            const newTransform = -currentIndex * itemWidth;
            carousel.style.transform = `translateX(${newTransform}px)`;

            // Disable/Enable buttons
            prevBtn.disabled = currentIndex === 0;
            nextBtn.disabled = currentIndex >= totalItems - itemsToShow;

            prevBtn.classList.toggle('opacity-50', prevBtn.disabled);
            nextBtn.classList.toggle('opacity-50', nextBtn.disabled);
        }

        nextBtn.addEventListener('click', () => {
            if (currentIndex < totalItems - itemsToShow) {
                currentIndex++;
                updateCarousel();
            }
        });

        prevBtn.addEventListener('click', () => {
            if (currentIndex > 0) {
                currentIndex--;
                updateCarousel();
            }
        });

        window.addEventListener('resize', () => {
            itemsToShow = window.innerWidth < 768 ? 1 : (window.innerWidth < 1024 ? 2 : 4);
            // Reset index if it's out of bounds after resize
            if (currentIndex > totalItems - itemsToShow) {
                currentIndex = totalItems - itemsToShow;
            }
            updateCarousel();
        });

        // Initial setup
        updateCarousel();
    }
});
